} from 'antd'

import MenuBar from '../components/MenuBar';
import TempAttrDropdown from '../components/TempAttrDropdown';
import CanvasJSReact from '../canvas/canvasjs.react';
import { getCountry, getLatitude, getLongitude,
        getDateValues, getAttrValues, getWeatherDescriptions, getBestCity } from '../fetcher'
const { Column, ColumnGroup } = Table;
const { Option } = Select;

var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

const weatherDescColumns = [
  {
    title: 'Time of Day',
    dataIndex: 'morning',
    key: 'morning',
    sorter: (a, b) => a.city_name.localeCompare(b.city_name),
  },
  {
    title: 'Weather Description',
    dataIndex: 'weather_description',
    key: 'weather_description',
  }
];

const attributeStatisticsColumns = [
  {
    title: 'Time of Day',
    dataIndex: 'timeOfDay',
    key: 'timeOfDay',
    sorter: (a, b) => a.city_name.localeCompare(b.city_name),
  },
  {
    title: 'Average',
    dataIndex: 'avg_attr',
    key: 'avg_attr',
  },
  {
    title: 'Minimum',
    dataIndex: 'min_attr',
    key: 'min_attr'

  },
  {
    title: 'Maximum',
    dataIndex: 'max_attr',
    key: 'max_attr'
  }
];

class ComparePage extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
      city1: "",
      country1: "",
      lat1: "",
      long1: "",
      city2: "",
      country2: "",
      lat2: "",
      long2: "",
      country2: "",
      startDateString: "",
      endDateString: "",
      startYear: 2012,
      startMonth: 10,
      startDay: 1,
      startHour: 0,
      endYear: 2017,
      endMonth: 11,
      endDay: 29,
      endHour: 0,
      weatherAttr: "Temperature",
      attrStatsResults1: [],
      attrStatsResults2: [],
      weatherDescResults1: [],
      weatherDescResults2: [],
      dateValueResults1: [],
      dateValueResults2: [],
      dateValueDataPoints1: [],
      dateValueDataPoints2: [],
      bestCityResult: "",
      errorMessage: "",
      loadingMessage: "",
      pagination: null  
    }

    this.setCity1 = this.setCity1.bind(this)
    this.setCountry1 = this.setCountry1.bind(this)
    this.setLat1 = this.setLat1.bind(this)
    this.setLong1 = this.setLong1.bind(this)
    this.setCity2 = this.setCity2.bind(this)
    this.setCountry2 = this.setCountry2.bind(this)
    this.setLat2 = this.setLat2.bind(this)
    this.setLong2 = this.setLong2.bind(this)
    this.setStartDate = this.setStartDate.bind(this)
    this.setStartHour = this.setStartHour.bind(this)
    this.setEndDate = this.setEndDate.bind(this)
    this.setEndHour = this.setEndHour.bind(this)
    this.setWeatherAttribute = this.setWeatherAttribute.bind(this)
    this.submitAnalysis = this.submitAnalysis.bind(this)
    this.processDataPoints = this.processDataPoints.bind(this)
  }


  setCity1(value) {
    // set city in state 
    this.setState({ city1: value })

    // set country, lat, and long as result
    this.setCountry1(value)
    this.setLat1(value)
    this.setLong1(value) 
  }

  setCountry1(value) {
    getCountry(value).then(res => {
      this.setState({ country1: res.results })
    })
  }

  setLat1(value) {
    getLatitude(value).then(res => {
      this.setState({ lat1: res.results})
    })
  }

  setLong1(value) {
    getLongitude(value).then(res => {
      this.setState({ long1: res.results})
    })
  }

  setCity2(value) {
      // set city in state 
      this.setState({ city2: value })

      // set country, lat, and long as result
      this.setCountry2(value)
      this.setLat2(value)
      this.setLong2(value) 
  }

  setCountry2(value) {
    getCountry(value).then(res => {
      this.setState({ country2: res.results })
    })
  }

  setLat2(value) {
    getLatitude(value).then(res => {
      this.setState({ lat2: res.results})
    })
  }

  setLong2(value) {
    getLongitude(value).then(res => {
      this.setState({ long2: res.results})
    })
  }

  setStartDate(event) {
    // get substrings for year, month, day
    const fullString = event.target.value
    const year = fullString.substring(0, 4)
    const month = fullString.substring(5, 7)
    const day = fullString.substring(8, 10)

    // set values when converting to int 
    this.setState({ startDateString: fullString })
    this.setState({ startYear: parseInt(year) })
    this.setState({ startMonth: parseInt(month)})
    this.setState({ startDay: parseInt(day)})
  }

  setStartHour(event) {
    if (event.target.value > 23 || event.target.value < 0) {
      this.setState({ errorMessage: "Hour must be between 0-23."});
    } else {
      this.setState({ errorMessage: "" });
    }

    this.setState({startHour: parseInt(event.target.value)})
  }

  setEndDate(event) {
    // get substrings for year, month, day
    const fullString = event.target.value
    const year = fullString.substring(0, 4)
    const month = fullString.substring(5, 7)
    const day = fullString.substring(8, 10)

    // set values when converting to int 
    this.setState({ endDateString: fullString })
    this.setState({ endYear: parseInt(year) })
    this.setState({ endMonth: parseInt(month)})
    this.setState({ endDay: parseInt(day)})
  }

  setEndHour(event) {
    if (event.target.value > 23 || event.target.value < 0) {
      this.setState({ errorMessage: "Hour must be between 0-23."});
    } else {
      this.setState({ errorMessage: "" });
    }

    this.setState({endHour: parseInt(event.target.value)})
  }

  setWeatherAttribute(value) {
    this.setState({ weatherAttr: value })
  }

  processDataPoints(results, oneOrTwo) {
    // clear current data points 
    if (oneOrTwo == "one") {
      this.setState({ dateValueDataPoints1: [] })
    } else {
      this.setState({ dateValueDataPoints2: [] })
    }

    var temp = []

    // process all results
    for (var i = 0; i < results.length; i++) {
      // create new item and get original date and attribute values 
      var item = {}
      item['x'] = new Date(results[i].date)
      item['y'] = results[i].attr

      // push into temporary array 
      temp.push(item);
    }

    // set back current data points
    if (oneOrTwo == "one") {
      this.setState({ dateValueDataPoints1: temp })
    } else {
      this.setState({ dateValueDataPoints2: temp })
    }

  }

  submitAnalysis() {
    // get to be empty before processing 
    this.setState(
      {
        attrStatsResults1: [],
        attrStatsResults2: [],
        weatherDescResults1: [],
        weatherDescResults2: [],
        bestCityResult: "",
        loadingMessage: "Loading..."
      }, 
      function() {
        // get attribute values for city1
        getAttrValues(this.state.city1, this.state.weatherAttr, this.state.startDateString, this.state.endDateString).then(res => {
          this.setState({ attrStatsResults1 : res.results })
        })

        // get attribute values for city2
        getAttrValues(this.state.city2, this.state.weatherAttr, this.state.startDateString, this.state.endDateString).then(res => {
          this.setState({ attrStatsResults2 : res.results })
        })

        // get weather descriptions for city1
        getWeatherDescriptions(this.state.city1, this.state.startDateString, this.state.endDateString).then(res => {
          this.setState({ weatherDescResults1: res.results })
        })

        // get weather descriptions for city2
        getWeatherDescriptions(this.state.city2, this.state.startDateString, this.state.endDateString).then(res => {
          this.setState({ weatherDescResults2: res.results })
        })

        // get date values for city1
        getDateValues(this.state.city1, this.state.weatherAttr, this.state.startDateString, this.state.endDateString).then(res => {
           this.processDataPoints(res.results, "one")
        })

        // get date values for city2
        getDateValues(this.state.city2, this.state.weatherAttr, this.state.startDateString, this.state.endDateString).then(res => {
          this.processDataPoints(res.results, "two")
       })

       // get best city 
       getBestCity(this.state.city1, this.state.city2, this.state.weatherAttr, this.state.startDateString, this.state.endDateString).then(res => {
        this.setState({ bestCityResult: res.results})
       })

       this.setState({ loadingMessage: "" })
      }
    );
  }


  render() {
    const dateValueGraphOptions = {
      animationEnabled: true,
      exportEnabled: true,
      theme: "light2", // "light1", "dark1", "dark2",
      title: {
        text: "Date vs Attribute for Selected Cities"
      },
      axisY: {
        title: this.state.weatherAttr,
        interval: 5
      },
      axisX: {
        title: "Date",
        // intervalType: "hour",
        valueFormatString: "DD-MM-YY HH"
      },
      data: [{
        type: "line",
        name: this.state.city1,
        showInLegend: true,
        xValueType: "dateTime",
        dataPoints: this.state.dateValueDataPoints1
      }, 
    {
      type: "line",
        name: this.state.city2,
        showInLegend: true,
        xValueType: "dateTime",
        dataPoints: this.state.dateValueDataPoints2
    }]
    }

    return (
      <div>
        <MenuBar />
        <Form style={{ width: '80vw', margin: '0 auto', marginTop: '5vh' }}>
          <h3> Compare Page </h3>
          <h5>Select two cities, as well as a start date and end date and a temperature attribute, and this application will return comparitive statistics about 
            the attribute in both places over the given time period. </h5>
        </Form>
        <Form style={{ width: '80vw', margin: '0 auto', marginTop: '5vh' }}>
                    <Row>
                    <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                            <label><strong>City 1</strong> &nbsp;&nbsp;</label>
                    <Select defaultValue="Select" style={{ width: 170 }} onChange={this.setCity1}>
                                <Option value="Vancouver">Vancouver</Option>
                                <Option value="Portland">Portland</Option>
                                <Option value="San Francisco">San Francisco</Option>
                                <Option value="Seattle">Seattle</Option>
                                <Option value="Los Angeles">Los Angeles</Option>
                                <Option value="San Diego">San Diego</Option>
                                <Option value="Las Vegas">Las Vegas</Option>
                                <Option value="Pheonix">Pheonix</Option>
                                <Option value="Albuquerque">Albuquerque</Option>
                                <Option value="Denver">Denver</Option>
                                <Option value="San Antonio">San Antonio</Option>
                                <Option value="Dallas">Dallas</Option>
                                <Option value="Houston">Houston</Option>
                                <Option value="Kansas City">Kansas City</Option>
                                <Option value="Minneapolis">Minneapolis</Option>
                                <Option value="Saint Louis">Saint Louis</Option>
                                <Option value="Chicago">Chicago</Option>
                                <Option value="Nashville">Nashville</Option>
                                <Option value="Indianapolis">Indianapolis</Option>
                                <Option value="Atlanta">Atlanta</Option>
                                <Option value="Detriot">Detroit</Option>
                                <Option value="Jacksonville">Jacksonville</Option>
                                <Option value="Charlotte">Charlotte</Option>
                                <Option value="Miami">Miami</Option>
                                <Option value="Pittsburgh">Pittsburgh</Option>
                                <Option value="Toronto">Toronto</Option>
                                <Option value="Philadelphia">Philadelphia</Option>
                                <Option value="New York">New York</Option>
                                <Option value="Montreal">Montreal</Option>
                                <Option value="Boston">Boston</Option>
                                <Option value="Beersheba">Beersheeba</Option>
                                <Option value="Tel Aviv District">Tel Aviv District</Option>
                                <Option value="Elliat">Elliat</Option>
                                <Option value="Haifa">Haifa</Option>
                                <Option value="Nahariyya">Nahariyya</Option>
                                <Option value="Jerusalem">Jerusalem</Option>
                            </Select> 
                            <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                            <p>Country: {this.state.country1}</p>
                            <p>Latitude: {this.state.lat1}</p>
                            <p>Longitude: {this.state.long1}</p>
                            </FormGroup></Col>
                        </FormGroup></Col>


                        <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                            <label><strong>City 2</strong> &nbsp;&nbsp;</label>
                    <Select defaultValue="Select" style={{ width: 170 }} onChange={this.setCity2}>
                                <Option value="Vancouver">Vancouver</Option>
                                <Option value="Portland">Portland</Option>
                                <Option value="San Francisco">San Francisco</Option>
                                <Option value="Seattle">Seattle</Option>
                                <Option value="Los Angeles">Los Angeles</Option>
                                <Option value="San Diego">San Diego</Option>
                                <Option value="Las Vegas">Las Vegas</Option>
                                <Option value="Pheonix">Pheonix</Option>
                                <Option value="Albuquerque">Albuquerque</Option>
                                <Option value="Denver">Denver</Option>
                                <Option value="San Antonio">San Antonio</Option>
                                <Option value="Dallas">Dallas</Option>
                                <Option value="Houston">Houston</Option>
                                <Option value="Kansas City">Kansas City</Option>
                                <Option value="Minneapolis">Minneapolis</Option>
                                <Option value="Saint Louis">Saint Louis</Option>
                                <Option value="Chicago">Chicago</Option>
                                <Option value="Nashville">Nashville</Option>
                                <Option value="Indianapolis">Indianapolis</Option>
                                <Option value="Atlanta">Atlanta</Option>
                                <Option value="Detriot">Detroit</Option>
                                <Option value="Jacksonville">Jacksonville</Option>
                                <Option value="Charlotte">Charlotte</Option>
                                <Option value="Miami">Miami</Option>
                                <Option value="Pittsburgh">Pittsburgh</Option>
                                <Option value="Toronto">Toronto</Option>
                                <Option value="Philadelphia">Philadelphia</Option>
                                <Option value="New York">New York</Option>
                                <Option value="Montreal">Montreal</Option>
                                <Option value="Boston">Boston</Option>
                                <Option value="Beersheba">Beersheeba</Option>
                                <Option value="Tel Aviv District">Tel Aviv District</Option>
                                <Option value="Elliat">Elliat</Option>
                                <Option value="Haifa">Haifa</Option>
                                <Option value="Nahariyya">Nahariyya</Option>
                                <Option value="Jerusalem">Jerusalem</Option>
                            </Select> 
                            <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                            <p>Country: {this.state.country2}</p>
                            <p>Latitude: {this.state.lat2}</p>
                            <p>Longitude: {this.state.long2}</p>
                            </FormGroup></Col>
                        </FormGroup></Col>
                    </Row>
                    <Row>
                    <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                          <label><strong>Start Date (Min: 10/01/2012)</strong></label>
                          <FormInput type="date" onChange={this.setStartDate}></FormInput>
                          <FormInput placeholder="HH" onChange={this.setStartHour} />
                        </FormGroup></Col>
                      <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                        <label><strong>End Date (Max: 11/29/2017)</strong></label>
                        <FormInput type="date" onChange={this.setEndDate}></FormInput>
                        <FormInput placeholder="HH" onChange={this.setEndHour} />
                      </FormGroup></Col>
                        <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                            <label><strong>Weather Attribute</strong></label>
                            <Select defaultValue="Select" style={{ width: 170 }} onChange={this.setWeatherAttribute}>
                                <Option value="temperature">Temperature</Option>
                                <Option value="pressure">Pressure</Option>
                                <Option value="humidity">Humidity</Option>
                                <Option value="wind_direction">Wind Direction</Option>
                                <Option value="wind_speed">Wind Speed</Option>
                            </Select>
                        </FormGroup></Col>
                    </Row>
                    <Row>
                    <Col flex={3}>
                      <FormGroup style={{ width: '10vw' }}>
                          <Button style={{ marginTop: '4vh' }} onClick={this.submitAnalysis}>See Analysis!</Button>
                        </FormGroup>
                    </Col>
                    <Col flex={3}>
                      <FormGroup style={{ width: '10vw' }}>
                        <p style={{color:"red"}} >{this.state.errorMessage}</p>
                      </FormGroup>
                    </Col>
                    <Col flex={3}>
                      <FormGroup style={{ width: '10vw' }}>
                        <p style={{color:"red"}} >{this.state.loadingMessage}</p>
                      </FormGroup>
                    </Col>
                  </Row>
                </Form>

               {(this.state.attrStatsResults1.length !== 0) && <div>
                <Form style={{ width: '80vw', margin: '0 auto', marginTop: '5vh' }}>
                  <Row>
                  <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                  <label>Statistics Over Time Period: {this.state.weatherAttr}, {this.state.city1} </label>

                  <Table dataSource={this.state.attrStatsResults1} columns={attributeStatisticsColumns} pagination={{ pageSizeOptions:[5, 10], defaultPageSize: 5, showQuickJumper:true }}/>
                  </FormGroup>
                  </Col>
                  <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                  <label>Statistics Over Time Period: {this.state.weatherAttr}, {this.state.city2} </label>
                    <Table dataSource={this.state.attrStatsResults2} columns={attributeStatisticsColumns} pagination={{ pageSizeOptions:[5, 10], defaultPageSize: 5, showQuickJumper:true }}/>
                  </FormGroup></Col>
                  </Row></Form></div>
              }
              {(this.state.weatherDescResults1.length !== 0) && <div>
                <Form style={{ width: '80vw', margin: '0 auto', marginTop: '5vh' }}>
                  <Row>
                  <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                  <label><strong>Most Likely Weather Description In: {this.state.city1} </strong></label>

                  <Table dataSource={this.state.weatherDescResults1} columns={weatherDescColumns} pagination={{ pageSizeOptions:[5, 10], defaultPageSize: 5, showQuickJumper:true }}/>
                  </FormGroup>
                  </Col>
                  <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                  <label><strong>Most Likely Weather Description In: {this.state.city2}</strong></label>
                    <Table dataSource={this.state.weatherDescResults2} columns={weatherDescColumns} pagination={{ pageSizeOptions:[5, 10], defaultPageSize: 5, showQuickJumper:true }}/>
                  </FormGroup></Col>
                  </Row>

                </Form></div>
              }
        <div>
			    {(this.state.dateValueDataPoints1.length !== 0) && <CanvasJSChart options = {dateValueGraphOptions}/>}
		    </div>
        {(this.state.bestCityResult !== "") && <div>
                <Form style={{ width: '80vw', margin: '0 auto', marginTop: '5vh' }}>
                  <Row>
                  <label><strong>Best City: {this.state.bestCityResult}</strong></label>
                  </Row>
                  <Row>
                  <label>How do we decide this? The city with the higher average attribute values over the period of time is chosen as the 'best'. Good if you like it hot, humid, and windy!</label>
                  </Row>

                </Form></div>
          }
      </div>
    )
  }
}

export default ComparePage
