      <div>
        <MenuBar />
        <Form style={{ width: '80vw', margin: '0 auto', marginTop: '5vh' }}>
        <h3> Welcome to 'How's the Weather?' Application! </h3>
        <h5> To get date suggestions for your event, visit the Suggest page. To compare weather 
          metrics between different cities, visit the Compare page. To get crime data alongside weather information, visit the Crimes page. 
        </h5>
        </Form>



      </div>
      <div id="map" style={{ position: 'absolute', top: '0', bottom: '0', width: '100%' }}></div>
      </body>
      </html>
    )
  }
}

export default HomePage
