import React from 'react';
import { Form, FormInput, FormGroup, Button, } from "shards-react";


import {
    Table,
    Row,
    Col,
    Select,
    Slider

} from 'antd'

import MenuBar from '../components/MenuBar';
import CanvasJSReact from '../canvas/canvasjs.react';

import { crimeTypes } from '../components/CrimeTypeArray';
import { getWeatherFromCrime, getGroupCrime, getGroupCrimeMonth, getGroupCrimeYear, getTopCrime
      , getComparisonCrime, getWeatherCrime, getCrimeAttractions } from '../fetcher'
const { Column, ColumnGroup } = Table;
const { Option } = Select;

var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

const weatherFromCrimeColumns = [
  {
    title: 'Most Common Weather Description',
    dataIndex: 'weather_description',
    key: 'weather_description',
  },
  {
    title: 'Worst Season',
    dataIndex: 'Worst_Season',
    key: 'Worst_Season',
  },
  {
    title: 'Average Temperature',
    dataIndex: 'Average_Temperature',
    key: 'Average_Temperature',
  },
  {
    title: 'Average Humidity',
    dataIndex: 'Average_Humidity',
    key: 'Average_Humidity'

  },
  {
    title: 'Average Pressure',
    dataIndex: 'Average_Pressure',
    key: 'Average_Pressure'
  }
];

const topCrimeColumns = [
  {
    title: 'Crime',
    dataIndex: 'crime_description',
    key: 'crime_description',
  },
  {
    title: 'Average Temperature',
    dataIndex: 'Average_Temperature',
    key: 'Average_Temperature',
  },
  {
    title: 'Average Humidity',
    dataIndex: 'Average_Humidity',
    key: 'Average_Humidity'

  },
  {
    title: 'Average Pressure',
    dataIndex: 'Average_Pressure',
    key: 'Average_Pressure'
  }
];

const attractionColumns = [ 
  // {
  //   title: 'Low Occurence of Crime Season',
  //   dataIndex: 'season',
  //   key: 'season',
  //   sorter: (a, b) => a.season.localeCompare(b.season)
  // },
  {
    title: 'Attraction',
    dataIndex: 'attraction_name',
    key: 'attraction_name',
    sorter: (a, b) => a.day_number - b.day_number

  },
  {
    title: 'Website',
    dataIndex: 'website',
    key: 'website',
    render: (text, record) => <a href={text} target="_blank">Link</a>
  },
  {
    title: 'Photo',
    dataIndex: 'photo',
    key: 'photo',
    render: (text) => <img src={text} referrerPolicy="no-referrer" alt={null} style={{height:'15vh'}}></img>

  }
];

class CrimesPage extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
        crimeTypeQuery: "Thefts",
        compareCrimeTypeQuery1: "Thefts",
        compareCrimeTypeQuery2: "Fraud",
        compareCrimeStartDateQuery: '2012-10-01',
        compareCrimeEndDateQuery: '2017-11-29',
        startDateString: '2012-10-01',
        endDateString: '2017-11-29',

        temperatureMin: -24.0,
        temperatureMax: 120.0, 
        humidityMin: 0.0,
        humidityMax: 100.0,
        pressureMin: 0.0, 
        pressureMax: 1000.0,


        weatherFromCrimeResults: [],
        hourBarChartResults: [],
        monthBarChartResults: [],
        yearBarChartResults: [],
        comparisonResults: [],
        topCrimeResults: [],
        crimeFromWeatherResults: [],
        attractionResults: [],

        loadingOption1: "",
        loadingOption2: "",
        loadingOption3: "",
        loadingOption4: ""
    }

    this.setStartDate = this.setStartDate.bind(this)
    this.setEndDate = this.setEndDate.bind(this)

    this.handleCrimeTypeQueryChange = this.handleCrimeTypeQueryChange.bind(this)

    this.handleCompareCrimeTypeQuery1Change = this.handleCompareCrimeTypeQuery1Change.bind(this)
    this.handleCompareCrimeTypeQuery2Change = this.handleCompareCrimeTypeQuery2Change.bind(this)
    this.handleCompareCrimeStartDateQueryChange = this.handleCompareCrimeStartDateQueryChange.bind(this)
    this.handleCompareCrimeEndDateQueryChange = this.handleCompareCrimeEndDateQueryChange.bind(this)

    this.handleTemperatureChange = this.handleTemperatureChange.bind(this);
    this.handleHumidityChange = this.handleHumidityChange.bind(this);
    this.handlePressureChange = this.handlePressureChange.bind(this);

    this.handleTopCrimeSubmit = this.handleTopCrimeSubmit.bind(this)
    this.handleCompareCrimeSubmit = this.handleCompareCrimeSubmit.bind(this)
    this.handleWeatherFromCrimeSubmit = this.handleWeatherFromCrimeSubmit.bind(this)
    this.handleCrimeFromWeatherSubmit = this.handleCrimeFromWeatherSubmit.bind(this)
  }

  setStartDate(event) {
    this.setState({ startDateString: event.target.value })
  }

  setEndDate(event) {
    this.setState({ endDateString: event.target.value })
  }

  handleCrimeTypeQueryChange(value) {
    this.setState({ crimeTypeQuery: value })
  }

  handleCompareCrimeTypeQuery1Change(value) {
    this.setState({ compareCrimeTypeQuery1: value })
  }

  handleCompareCrimeTypeQuery2Change(value) {
    this.setState({ compareCrimeTypeQuery2: value })
  }

  handleCompareCrimeStartDateQueryChange(event) {
    this.setState({ compareCrimeStartDateQuery: event.target.value })
  }

  handleCompareCrimeEndDateQueryChange(event) {
    this.setState({ compareCrimeEndDateQuery: event.target.value })
  }

  handleWeatherDescriptionQueryChange(value) {
    this.setState({weatherDescriptionQuery: value})
  }

  handleTemperatureChange(value) {
    this.setState({ temperatureMin: value[0] })
    this.setState({ temperatureMax: value[1] })
  }

  handleHumidityChange(value) {
      this.setState({ humidityMin: value[0] })
      this.setState({ humidityMax: value[1] })
  }

  handlePressureChange(value) {
      this.setState({ pressureMin: value[0] })
      this.setState({ pressureMax: value[1] })
  }

  handleWeatherFromCrimeSubmit() {
    // set loading message 
    this.setState({ loadingOption1: "Loading..." })
    // set to blank before processing 
    this.setState({ weatherFromCrimeResults: [] })
    this.setState({ hourBarChartResults: [] })
    this.setState({ monthBarChartResults: [] })
    this.setState({ yearBarChartResults: [] })

    getWeatherFromCrime(this.state.crimeTypeQuery).then(res => {
      this.setState({ weatherFromCrimeResults: res.results})
    })

    getGroupCrime(this.state.crimeTypeQuery).then(res => {
      this.setState({ hourBarChartResults: res.results })
    })

    getGroupCrimeMonth(this.state.crimeTypeQuery).then(res => {
      this.setState({ monthBarChartResults: res.results })
    })

    getGroupCrimeYear(this.state.crimeTypeQuery).then(res => {
      this.setState({ yearBarChartResults: res.results })
      this.setState({ loadingOption1: "" })
    })

  }

  handleCompareCrimeSubmit() {
    this.setState({ loadingOption4 : "Loading..." })
    // set to blank before query
    this.setState({ comparisonResults: [] })

    getComparisonCrime(this.state.compareCrimeStartDateQuery, this.state.compareCrimeEndDateQuery, this.state.compareCrimeTypeQuery1, this.state.compareCrimeTypeQuery2).then(res => {
      this.setState({ comparisonResults: res.results })
      this.setState({ loadingOption4: "" })
    })
  }

  handleTopCrimeSubmit() {
      // set to blank before query
      this.setState({ topCrimeResults: [] })
      this.setState({ loadingOption3: "Loading..." })

    getTopCrime(this.state.startDateString, this.state.endDateString).then(res => {
      this.setState({ topCrimeResults: res.results })
      this.setState({ loadingOption3: "" })
    })
  }

  handleCrimeFromWeatherSubmit() {
    this.setState({ loadingOption2: "Loading..." })
      // set to blank before query
      this.setState({ crimeFromWeatherResults: [] })
      this.setState({ attractionResults: []})

    getWeatherCrime(this.state.temperatureMin, this.state.temperatureMax, this.state.pressureMin, this.state.pressureMax, this.state.humidityMin, this.state.humidityMax).then(res => {
      this.setState({ crimeFromWeatherResults: res.results })
    })

    getCrimeAttractions(this.state.temperatureMin, this.state.temperatureMax, this.state.pressureMin, this.state.pressureMax, this.state.humidityMin, this.state.humidityMax).then(res => {
      this.setState({ attractionResults: res.results })
      this.setState({ loadingOption2: "" })
    })
  }



