			axisX: {
				title: "Crime Type",
				reversed: true,
			},
			axisY: {
				title: "Number of Occurences",
				includeZero: true,
				labelFormatter: this.addSymbols
			},
			data: [{
				type: "bar",
				dataPoints: this.state.comparisonResults
			}]
		}
    const crimeFromWeatherBarChartOptions = {
			animationEnabled: true,
