import React, { Component } from 'react';
import USAMap from "react-usa-map";
import { Form, FormInput, FormGroup, Button, } from "shards-react";
import {
  Table,
  Pagination,
  Select,
  Row
} from 'antd'

import MenuBar from '../components/MenuBar';
import { getNewsRankings } from '../fetcher'
const { Column, ColumnGroup } = Table;
const { Option } = Select;


class MapPage extends React.Component {

    // MAP HANDLER FROM API 
    mapHandler = (event) => {
        // alert(event.target.dataset.name);
        console.log(event.target.dataset.className)
        this.handleStateSelected(event.target.dataset.name)
    };

  constructor(props) {
    super(props)

    this.state = {
        stateResults: [],
    }

    this.handleStateSelected = this.handleStateSelected.bind(this)

  }

  handleStateSelected(stateCode) {
    // call function 
    getNewsRankings(stateCode).then(res => {
        this.setState({ stateResults: res.results })
    })

}

  componentDidMount() {
    //var map = new mapboxgl.Map({
    //  container: 'map',
    //  style: 'mapbox://styles/mapbox/streets-v11'
    //});
  }





  render() {

    return (
        <div className="App">
        <MenuBar />
        <Form style={{ width: '80vw', margin: '0 auto', marginTop: '5vh' }}>
          <Row> 
            <USAMap onClick={this.mapHandler} />
          </Row>
          <Row>

          </Row>

        </Form>
        </div>
      );
  }
}

export default MapPage
